// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;



contract InsurancePool {
  enum InsuranceType {Simple, Extended}

  struct Insurance {
    InsuranceType insuranceType;
    address payable customer;
    uint32 depositAmmount;
    uint256 createdAt;
    uint256 lastUpdatedAt;
    bool isNewEntity;
  }

  address public owner;
  uint32 public totalPoolSize;
  mapping(address => Insurance) public holdings;

  constructor() public {
    owner = msg.sender;
  }

  function createNewCustomer(address payable newCustomer, uint32 ammount, InsuranceType insuranceType) public payable {
    // check if user already exists
    require(holdings[newCustomer].isNewEntity == false, "user already exists");
    uint256 nowTimestamp = block.timestamp;
    Insurance storage i = holdings[newCustomer];
    i.depositAmmount += ammount;
    i.insuranceType = insuranceType;
    i.customer = newCustomer;
    i.createdAt = nowTimestamp;
    i.lastUpdatedAt = nowTimestamp;
    i.isNewEntity = true;

    totalPoolSize += ammount;
  }

  // get insurance information for user 
  function getInsuranceInformation(address customer) public view returns (InsuranceType, uint32, uint256, uint256) {
    return (holdings[customer].insuranceType, holdings[customer].depositAmmount, holdings[customer].createdAt, holdings[customer].lastUpdatedAt);
  }

  // deposit ammount to subscription
  function depositToInsurance(address customer, uint32 ammount) public returns (uint32, uint256) {
    // verify that user exists before update

    require(holdings[customer].customer == customer);
    uint256 nowTimestamp = block.timestamp;
    Insurance storage i = holdings[customer];
    i.depositAmmount += ammount;
    i.lastUpdatedAt = nowTimestamp;
    return (i.depositAmmount, i.lastUpdatedAt);
  }
}
